import React,{useEffect,useState} from "react";
import {Link,useHistory} from 'react-router-dom' 
import './style.css';
import { FiPower,FiEdit,FiTrash2 } from 'react-icons/fi'
import api from "../../services/api";

import logo from "../../assets/logo.svg";

export default function Books(){

    const [books, setBooks] = useState([]);
    const [page, setPage] = useState([]);
    const userName = localStorage.getItem('userName');
    const accessToken = localStorage.getItem('accessToken');
    const history = useHistory(); 

    const authorization = {
      headers:{
          Authorization: `Bearer ${accessToken}`
      }
  }
   
      useEffect(() => {
         fetchMoreBooks();
      },[accessToken]);

      async function  fetchMoreBooks() {
         api.get(`api/Book/v1/asc/4/${page}`,authorization).then(response => {
            setBooks([...books,...response.data.list]);
            setPage(page + 1);
         })
      }

      async function deleteBook(id) {
         try {
            await api.delete(`api/Book/v1/${id}`,authorization);

           setBooks(books.filter(book => book.id !== id));
         } catch (error) {
            alert('Delete failed! Try again!');
         }
      }
      async function editBook(id) {
         try {
            history.push(`book/new/${id}`)
         } catch (error) {
            alert('Edit book! Try again!');
         }
      }
      async function logout() {
         try {
            await api.get('api/auth/v1/revoke',authorization);
           localStorage.clear();
           history.push('/');
         } catch (error) {
            alert('Logout failed! Try again!');
         }
      }

   return (
      <div className="book-container">
         <header>
            <img src={logo} alt="Erudio"></img>
            <span>Welcome,<strong>{userName.toUpperCase()}</strong>!</span>
            <Link className="button" to="book/new/0">Add New Book</Link>
            <button onClick={logout} type="button">
               <FiPower size={18} color="#251fc5">

               </FiPower>
            </button>
         </header>
         <h1>Register Books</h1>
         <ul>
            {books.map(book => (
               <li key={book.id}>
               <strong>Title:</strong>
               <p>{book.title}</p>
               <strong>Author:</strong>
               <p>{book.author}</p> 
               <strong>Price:</strong>
               <p>{Intl.NumberFormat('pt-br',{style:'currency',currency:'BRL'}).format(book.price)}</p>
               <strong>Release Date:</strong>
               <p>{Intl.DateTimeFormat('pt-BR').format(new Date(book.launchDate))}</p>

               <button onClick={() => editBook(book.id)} type="button">
                  <FiEdit size={20} color="#251fc5"></FiEdit>
               </button>

               <button onClick={()=> deleteBook(book.id)} type="button">
                  <FiTrash2 size={20} color="#251fc5"></FiTrash2>
               </button>
            </li>
            ))}
         </ul>
         <button className="button" onClick={fetchMoreBooks} type="button">Load More</button>
      </div>
   )
}
