import React,{useEffect,useState} from "react";
import {Link,useHistory} from 'react-router-dom' 
import './style.css';
import { FiPower,FiEdit,FiTrash2 } from 'react-icons/fi'
import api from "../../services/api";

import logo from "../../assets/logo.svg";

export default function Books(){

    const [books, setBooks] = useState([]);
    const userName = localStorage.getItem('userName');
    const accessToken = localStorage.getItem('accessToken');
    const history = useHistory(); 
   
      useEffect(() => {
         api.get('api/Book/v1/asc/5/1',{
                headers:{
                    Authorization: `Bearer ${accessToken}`
                }
         }).then(response => {
            setBooks(response.data.list)
         })
      },[accessToken])

   return (
      <div className="book-container">
         <header>
            <img src={logo} alt="Erudio"></img>
            <span>Welcome,<strong>Alaor</strong>!</span>
            <Link className="button" to="book/new">Add New Book</Link>
            <button type="button">
               <FiPower size={18} color="#251fc5">

               </FiPower>
            </button>
         </header>
         <h1>Register Books</h1>
         <ul>
            <li>
               <strong>Title:</strong>
               <p>Docker deep dive</p>
               <strong>Author:</strong>
               <p>Nigel Poulton</p>
               <strong>Price:</strong>
               <p>R$: 47,90</p>
               <strong>Release date:</strong>
               <p>12/07/2017</p>

               <button type="button">
                  <FiEdit size={20} color="#251fc5"></FiEdit>
               </button>

               <button type="button">
                  <FiTrash2 size={20} color="#251fc5"></FiTrash2>
               </button>
            </li>
         </ul>
      </div>
   )
}
